renderpass.code:
#define S(v)   smoothstep(3./R.y,0.,v)
#define W(n,t) l * cos( ( fract(atan(U.y,U.x)*n/6.283 +.5+t -iTime ) -.5 )*6.283/n + vec2(0,1.57) ) // polar n-wrap

void mainImage( out vec4 O, vec2 u )
{
    vec2  R = iResolution.xy,
          U = ( 2.*u - R ) / R.y - vec2(0,.2), V;
     float l = length(U);
     V = W(6.,0.);

    O = vec4(.7 + .3* S( abs(abs(V.y)-.05) -.005 ) ) * S(l-.7);   // main disk + rails

    U.y += .35; l = length(U);                                    // small gear
    V = W(3.,.25);
    O += .5 * S(abs(V.y)-.03) *  S(l-.38);

    V.x -= .34;                                                   // pins
    O -=  S(length(V)-.01);

}
