void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord - (iResolution.xy / 2.);
    uv /= min(iResolution.x, iResolution.y) / 2.;

    float z = length(uv);
    z = sqrt(1. - z*z);

    vec3 sphere = vec3(uv, z);
    sphere *= smoothstep(0.001, 0.1, z);

    vec3 light = vec3(cos(iTime) * 1.5, 2.0, sin(iTime) * 1.5);
    light *= 1. / pow(distance(light, sphere), 2.);

    float lighting = dot(sphere, light);

    fragColor = vec4(lighting);
}
