bool    CONST=false; // 0: splats follow zoom  1: splats of constant size
bool    TEXT=false;  // 0: dots            1: texture splats
#define N     3      // neighborhood
vec2 R;

#define hash2(p) fract(sin( (p) * mat2(127.1,311.7,  269.5,183.3))*43758.5453123)
#define hash3(p) fract(sin( (p) * mat3(127.1,311.7,74.7,  269.5,183.3,246.1, 113.5,271.9,124.6))*43758.5453123)

vec4 points(vec2 U) {
    vec2 C = floor(U), X = fract(U), D,P;
    vec4 v = vec4(0);
    float r, w = fwidth(length(U))*R.y/360.;
    for (int k=0; k<N*N; k++) {     // visit neighbor cells
        D = vec2(k%N-N/2,k/N-N/2);
        P = D+hash2(C+D) -X;        // distance to local dot

        r = CONST 
            ? 3.*w  // splats of constant size
            :.2;             // splats follow zoom

        v += TEXT                   // add splat
               ?   smoothstep(4.*r,0.,length(P))
                   * texture(iChannel0,.5+.05*P/r)
               : vec4(smoothstep(r,0.,length(P)));
    }
    // only if we want to compensate big start size by inv weigth
    if (CONST) w *= 3./.2, v /= w*w;
    return v;
}

void mainImage( out vec4 O, vec2 U )
{
    R = iResolution.xy;
    if (U.x==R.x/2.-.5) { O = vec4(1,0,0,1); return; } // red separator
    U = 10.* ( 2.*U - R ) / R.y;
    CONST = U.x < 0.;
    TEXT  = U.y > 0.;
    O = vec4(0);    

    float scale = fract(iTime/2.);  // as long as seeds are intical
    U *= exp2(floor(scale))/exp2(scale);
#if 0                               // 2 layers
    O = mix( points(U), points(2.*U), fract(scale) );
#else                               // many layers
    for (float i=-1.; i<=2.; i++)
        O += points( exp2(i)*U ) * smoothstep(1.,0.,abs(scale-i)/2.);
#endif

    O = pow( O, vec4(1./2.2) );     // to sRGB
}
