#define time iTime

mat2 rot(float u) {
    return mat2(cos(u),sin(u),-sin(u),cos(u));
}

float HexDist(vec2 p) {
    p = abs(p);

    float c = dot(p, normalize(vec2(1,1.73)));

    c = max(c, p.x);
    return c;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv =  ( fragCoord -.5* iResolution.xy ) / iResolution.y;
    uv *= rot(time/5.);
   vec2 uvv = uv;
  uv = sin(uv*5.);
  vec3 color = clamp(step(0.5+sin(uvv.x*2.+uvv.y*1.5+6.*uv.y+time*10.)/10. ,vec3(HexDist(uv))),0.,1.);
  color.g = 0.;
  color.b = 0.;
    fragColor = vec4(
        color,
        1.0);
}
