void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

	float w = (0.8 - (uv.x)) * (iResolution.x / iResolution.y);
    float h = 0.2 - uv.y;
	float distanceFromCenter = sqrt(w * w + h * h) *30.5;

	float sinArg = distanceFromCenter - iTime * 10.0;
	float slope = cos(sinArg) ;
	vec4 color = texture(iChannel0, uv + normalize(vec2(w, h)) * slope * 0.01);

	fragColor = color;
}
