// Hash function by David Hoskins: https://www.shadertoy.com/view/4djSRW
float hash12(vec2 p)
{
	vec3 p3  = fract(vec3(p.xyx) * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z);
}

#define CYCLE 1.
#define RESOLUTION 128.
#define THRESHOLD .5

float ease(float t){
	//return t<.5 ? 2.*t*t : -1.+(4.-2.*t)*t;
    return t<.5 ? 8.*t*t*t*t : 1.-8.*(--t)*t*t*t;
    //return 1.-(--t)*t*t*t;
    //return t;
}

float complicatedNoise(float t, vec2 uv){
	float f1 = float(hash12(uv+floor(t)*10.) < THRESHOLD);
    float f2 = float(hash12(uv+floor(t+1.)*10.) < THRESHOLD);
    float f3 = float(hash12(uv+floor(t+2.)*10.) < THRESHOLD);
    return ((f1+f2)*.5+f3)*.5;
}

float getColor(vec2 uv){
    float tP = iTime/CYCLE+hash12(uv*10.);
    float tN = (iTime/CYCLE+1.)+hash12(uv*10.);
    // Output to screen
	float prev = complicatedNoise(tP, uv);
    float next = complicatedNoise(tN, uv);
    return mix(prev, next, ease(fract(tP)));
    //return vec4(prev);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord/iResolution.xy;
    uv.x *= iResolution.x/iResolution.y;
    uv *= RESOLUTION;
    float dist = 1./16.;
    vec2 movement = vec2(iTime)*vec2(1., 1.)*.1;
    //vec2 movement = vec2(0.);
    float background = getColor(floor(uv+movement));
    float foreground = getColor(floor(uv*dist+movement));

    //vec4 fgColor = vec4(0.63, 0.13, 0.94, 1.0);
    vec4 fgColor = vec4(.0);
    vec4 bgColor = vec4(0.63, 0.13, 0.94, 1.0);

    fragColor = mix(fgColor, bgColor, background*foreground);

    //fragColor = foreground;
}
