#define PI 3.1415926535897932384626433832795

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
	vec2 uv2 = vec2((uv.x+iTime*PI*0.125)+uv.y*0.125,0.1);

    vec2 uv1 = uv + vec2(sin((uv.y+iTime)*8.0)*0.02,sin((uv.y+iTime)*2.0)*0.2);

    // Output to screen
    fragColor = texture(iChannel0, uv1).x*
        uv.x*(1.0-uv.x)*uv.y*(1.0-uv.y)* 
        vec4((vec3(
            sin(-iTime),
            sin(-iTime+0.5*PI),
            sin(-iTime+PI)
            )+1.0)*0.5,1)
        *texture(iChannel1, uv2).x*(256.0+sin(iTime)*250.0);
}
