#define PI 3.14159265359
#define PI2 6.28318530718
#define E 2.7182818284
#define S01(t) sin(t) * .5 + .5
#define C01(t) cos(t) * .5 + .5
#define S(a,b,t)  mix(a, b, S01(t))
#define C(a,b,t)  mix(a, b, C01(t))
#define BPM 131.
#define BPS BPM/60.

float N21(vec2 p){
    p = fract(p*vec2(123.45, 678.92));
    p += dot(p, p+23.45);
    return fract(p.x*p.y);
}

vec2 N22(vec2 p){
    float n = N21(p);
    return vec2(n, N21(p+n));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float scale = 10.;
    float aa = scale/iResolution.y;
    float t = iTime;

    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
	uv *= scale;


    float m = 1.;
    float count = 20.;

    for(float i = 0.; i < count; i++){
        float i01 = i/count;
        float d = mix(.6,.35,i01);
        vec2 n = N22(vec2(i));
        float ti = i*d+t;
        float a = i*(PI2/count);
        float w = mix(1.,1.1,sin(a*PI2+t*5.)*.5+.5)*mix(1.,1.2,n.x);
        float s = 2.;//mix(.75,4.,i01);
        vec2 p = vec2(cos(a),sin(a)) * w * s;
        float l = length(p-uv)*mix(1.2,.6,n.x);
        float c = 1.-pow(E,2.-l*5.);
        m *= c;
    }

    float s = mix(.1, -.9, sin(t)*.5+.5);
    m = smoothstep(s, s-aa, m);

    vec3 col = vec3(m);

    // Output to screen
    fragColor = vec4(col,1.0);
}
