#define GR vec3(39,139,29)/255.
#define hue(h) clamp( abs( fract(h + vec4(3,2,1,0)/3.) * 6. - 3.) -1. , 0., 1.)

float rand(vec2 co) { 
    return fract(sin(dot(co.xy , vec2(12.9898, 78.233))) * 43758.5453);
} 

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 ruv = (fragCoord - iResolution.xy*.5)/iResolution.y;     

    vec3 vcol = texture(iChannel0, uv).rgb;
    vec3 bcol = texture(iChannel1, uv).rgb;

    vec3 vcs = abs(vcol - GR);
    float vcm = (vcs.r+vcs.g+vcs.b);
    float vw = clamp(vcm, 0., 1.);

    vec3 col = vw < .3 ? vec3(1.) : hue(iTime*.5 + rand(floor(ruv*100.))).rgb;

    col = mix(col, bcol, .9);

    fragColor = vec4(col,1.0);    
}
