void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = fragCoord.xy;
    vec3 c;
	vec3 spectrum;

    if (p.x < 1.0)
    {
        // spectrum history
        c = texture(iChannel0, vec2(p.x, p.y - 1.0) / iResolution.xy).rgb;
        c = mix(spectrum * vec3(1.0, 16.0, 256.0) * 2.0, c, min(1.0, p.y));
    }
    else
    {
        // color bars
        vec2 ref = vec2(0.0, p.x * 0.1 / iResolution.y);
        c = texture(iChannel0, ref).rgb;
        c = c - abs(p.y / iResolution.y - 0.5);
    }

    fragColor = vec4(c, 1.0);
}
