void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = fragCoord.xy/iResolution.xy;
    vec3 conversion = vec3(0.3, 0.59, 0.11);

	vec3 webcam_pixel = texture(iChannel0, p).rgb;

    float gray_scale = dot(webcam_pixel, conversion);

    if (gray_scale > 0.4) {
        fragColor = vec4(webcam_pixel, 1.0);
    } else {
    	fragColor = vec4(vec3(gray_scale), 1.0);        
    }
}
