void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   
float factor=1000.0;
 for (int j = 0; j < 2; j++) {      
factor=factor/9.0;
fragCoord.y=fragCoord.y+fragCoord.x/factor;
     fragCoord.x=fragCoord.y+fragCoord.y/factor;


    float wut = fract((190. + iTime) / 1000. * fragCoord.x * fragCoord.y);
    fragColor = vec4(wut, wut / 2., wut * 2., 1.0);
 }
}

