// 138 chars (proper scaling and nicer colors)

void mainImage(out vec4 a, vec2 u)
{
    a = u.xyxy / iResolution.x * 7. - 2.;
    for (u -= u; u++.x < 25.; )
        a.xy = a.xx * a.xy + a.yy * vec2(-a.y, a.x) + a.zw;
    a = abs(a.xyyy);
}


