//Random function taken from Book of Shaders
vec2 random(vec2 uv){
    uv = vec2( dot(uv,vec2(127.1,311.7)),
              dot(uv,vec2(269.5,183.3)) );
    return -1.0 + 2.0*fract(sin(uv)*43758.5453123);
}

float noise(vec2 uv) {
 	vec2 id = floor(uv);
    vec2 p = fract(uv); //Our sample point P located in a cell

    /*
		1. Generate a random gradient for every point in cell.
		Since each cell has 4 points we generate 4 rnd gradients.
	*/
    vec2 bl = random(id + vec2(0., 0.)); //bottom left
    vec2 br = random(id + vec2(1., 0.)); //bottom right
    vec2 tl = random(id + vec2(0., 1.)); //top left
    vec2 tr = random(id + vec2(1., 1.)); //top right

    /*In interpolation you usually use uv.x or uv.y to specify 
    	direction, however uv.x and y are linear by default so
		the interpolation will be linear. We don't want that.
		We want a smooth interpolation direction which we get by
		smoothstep(0., 1., f); or better: f*f*(3.-2.*f);
	*/
    vec2 smoothdir = p*p*(3.0-2.0*p);
    /*
		2. Perform Bi linear interpolation with a small twist.
		The interpolation is between the dot product of the cell
		vertices and the point P.
	*/
    //Bi-linear interpolation
    return mix(
        	   mix(dot(random(bl), p-vec2(0., 0.)), 
                   dot(random(br), p-vec2(1., 0.)), smoothdir.x),
        	   mix(dot(random(tl), p-vec2(0., 1.)), 
            	   dot(random(tr), p-vec2(1., 1.)), smoothdir.x)
           ,smoothdir.y);

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
	//uv.x *= iResolution.x/iResolution.y;

    vec3 col = vec3(0.0);
    vec2 newUv = vec2(uv*20.); //Konstigt att vi aldrig behövde fract här..
    //Tydligen inte eftersom vi nu ej vill repetera pattern, vi vill ha unika
    //uv coord i varje position pga vår random noise funktion.
    // Time varying pixel color
    col = vec3(noise(newUv)*.5+.5);

    // Output to screen
    fragColor = vec4(col,1.0);
}
