float Band(float t, float start, float end, float blur) {
    float step1 = smoothstep(start+blur, start-blur, t);
	float step2 = smoothstep(end-blur, end+blur, t);

    return step1*step2;
}

float Rect(vec2 uv, float horizontal, float vertical, float blur) {
	float band1 = Band(uv.x, -horizontal, horizontal, -blur);
    float band2 = Band(uv.y, -vertical, vertical, -blur);

	return band1*band2;
}

float Remap(float v, float fmn, float fmx, float tmn, float tmx) {
    return (v-fmn) / (fmx-fmn) * (tmx-tmn) + tmn;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    vec2 uv = fragCoord/iResolution.xy;

    uv -= .5;
    uv.y += .2*sin(iTime*-1.5+uv.x*8.);

    float mask = Rect(uv, .5, .1, Remap(uv.x, -.3, .5, .01, .25));

    fragColor = vec4(vec3(mask), 1.0);
}
