// Fabrice, -41c, 154c
void mainImage(out vec4 o, vec2 f)
{
    vec2 R = iResolution.xy,
         u = (f-R*.5) / R;
    u *= mat2(cos(sin(u*R/.64+iTime).y+vec4(0,33,11,0)));
	o += step(.01* abs(sin(iTime/8.*3.14)), abs(u.x) );
}

