float dist(vec2 a, vec2 b){
    return sqrt((a.x-b.x)*(a.x-b.x)+(a.y-b.y)*(a.y-b.y));
}

float random (vec2 st) {
    return fract(sin(dot(st.xy, vec2(11.9898,78.233)))*43758.5453123);
}

float noise2 (float a, float b) {
    float buff1 = fract((10000.f + abs(b + a)) * fract(a * fract((100.f + b) * fract(a * 0.0171 + b))));
    return(buff1 * 1.0038 - 0.00185);
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec2 floorxy = floor(uv*30.);

    float speed = 2.1;
    fragCoord.x += 30.0*sin(iTime*speed)+30.5*cos(888.5*floorxy.y);
    fragCoord.y += 30.0*cos(iTime*speed);


    float n = 0.9;
    float t = abs(sin(iTime));
    float max_d = dist(vec2(0.), iResolution.xy*0.5);
    float d = 1.-dist(fragCoord, iResolution.xy*0.5)/(max_d*0.7);

    float ra0 = noise2(fragCoord.x+iTime, fragCoord.y);

    float d2 = 1.-dist(fragCoord, vec2(iResolution.x*0.55, iResolution.y*0.4))/(max_d*0.5*t)+ra0;
    float r = smoothstep(0.5, 0.7, d)-smoothstep(0.7, 1.1, d)+d2*0.9-smoothstep(n, 0.0, d);
    float g = smoothstep(0.55, 0.8, d) -smoothstep(0.7, 1.0, d)+d2*0.5-smoothstep(n, 0.0, d);
    float b = smoothstep(0.6, 0.8, d) -smoothstep(0.7, 1.0, d)+d2*0.3-smoothstep(n, 0.0, d);
    vec3 col = vec3(r,g,b);
    fragColor = vec4(col,1.0);
}
