//Thanks to https://www.shadertoy.com/view/4tl3Dj for the stripes/sections!

float circle(vec2 uv, vec2 off, float r){
    float cir = pow(uv.x-off.x,2.) + pow(uv.y-off.y,2.);
    return step(cir,pow(r,2.));
}
float counter =0.;
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy - vec2(0.5,0.5);
    //Aspect ratio correction
    uv.x *= iResolution.x/iResolution.y;

    //Calculate the angle from the center + offset
    uv+=vec2(0.3,0.);
    vec2 uvo = uv + 0.1*vec2(cos(0.5*iTime),sin(0.5*iTime));
    float angle = atan(uvo.y,uvo.x)+(2.*iTime);

    //8 strips representing the offset reticle.
    vec3 intensity = vec3(step(0.01,sin(angle*8.0)) * circle(uvo,vec2(0),0.5));

    //Target position -> fixed
    vec2 uvx = vec2(0.0);

    //Draw target
    float cx = circle(uv,uvx,0.05);
    intensity+= vec3(cx,-cx,-cx);

    //How to send the target intersection to my scope shader -> target over black strip = hit ?

    //if(hit) square wave = 1.0 else square wave = 0.0

    //https://www.shadertoy.com/view/wldfWr <- This one


    //Set the final color
    fragColor = vec4(intensity, 1.0);

}
