const float k = 20.0;
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;
    float a = 0.0;

    uv*=2.0;

    float x = length(uv.xy);
    a = 1.0/(x*k) - (1.0/k);
    a = max(0.0,a);

    fragColor = vec4(vec3(a),1.0);
}
