vec2 mainSound( in int samp, float time )
{
	int t = int(time * 44000.0);
    t = ((t*6&t>>4|t*5&t>>7|t*3&t/1024)-int(time)%3)/2;
    return 0.8 * vec2(float(t & 0xff -128) / 128.);
}
