void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 u = 3.*(2.*fragCoord.xy-iResolution.xy)/iResolution.y;
    vec2 m = 8.*(iMouse.xy/iResolution.xy-.5);

    float pi = 3.14159265359;

    float e = acos(cos(iTime*pi/6.))*6./pi;
          e+= -clamp(e-1.,0.,1.)
              -clamp(e-3.,0.,1.)
              -clamp(e-5.,0.,1.);

    if(iMouse.z > .5) e = m.x;

    u = cos((atan(u.y,u.x)+pi)*e+vec2(0.,pi*.5))*pow(length(u),e);
	fragColor = texture( iChannel0, vec2(.125,1.)*.1*u+.4 );
}
