void mainImage( out vec4 Q, in vec2 U ){
    Q=texture(iChannel0,U/iResolution.xy);
}

void mainImage(out vec4 Q,in vec2 U){
    float k=U.y/iResolution.y;
    float x=U.x/iResolution.x*4.;
    float f=0.;

    //float y=U.y/iResolution.y;
    float y=sin(iTime/5.)*0.5+0.5;

    for(int i=0;i<256;i++){
   		y=x*y*(1.-y);
        f+=exp(-3e5*(y-k)*(y-k));//0.25*... (makes the chaos more clear)
        //f+=0.5*exp(-1e3*abs(y-k));
    }
    Q=mix(texture(iChannel0,U/iResolution.xy),vec4(1.-0.5*f),0.1);
}
