float dist(vec2 p1, vec2 p2)
{
	float dx = p1.x - p2.x;
    float dy = p1.y - p2.y;
    return sqrt(dx*dx + dy*dy);
}

#define PI 3.14159


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord/iResolution.xy - 0.5) * 2.0;

    uv.x *= iResolution.x / iResolution.y;

   	fragColor = vec4(0.0);

    float angle = atan(uv.y /1.0,uv.x) + PI;

    float d = dist(uv, vec2(0.0,0.0));

    float S = abs((iMouse.xy / iResolution.xy - 0.5).x);

    angle += pow(1.0 + S * 160.0,1.0+d) * PI - iTime/0.51;


    uv = vec2(cos(angle), sin(angle));


    if(uv.y > 0.2)
        fragColor = mix(vec4(0.0,0.0,0.0,0.0), vec4(0.0,0.0,1.0,0.0), 0.8);

    if (uv.y < -0.2)
        fragColor = mix(vec4(0.0,0.0,0.0,0.0), vec4(0.0,1.0,0.0,0.0), 0.8);


    fragColor /= sqrt(0.1+d/2.0) / 0.5;


}
