#define O 500
#define R iResolution.xy
#define A(U) texture(iChannel0,(U)/R)
#define B(U) texture(iChannel1,(U)/R)
#define Neighborhood vec4 n = A(U+vec2(0,1)), e = A(U+vec2(1,0)), s = A(U-vec2(0,1)), w = A(U-vec2(1,0)), m = 0.25*(n+e+s+w);
#define Main void mainImage (out vec4 Q, vec2 U) 
// Dave H
vec3 hash32(vec2 p)
{
	vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yxz+33.33);
    return fract((p3.xxy+p3.yzz)*p3.zyx);
}
vec4 hash44(vec4 p4)
{
	p4 = fract(p4  * vec4(.1031, .1030, .0973, .1099));
    p4 += dot(p4, p4.wzxy+33.33);
    return fract((p4.xxyz+p4.yzzw)*p4.zywx);
}

Main {
	if (iFrame >= O) discard;
    Q = A(U);
    Neighborhood

    Q.x += m.y-Q.y;
    Q.y += .4*Q.x;
    Q.zw = 0.25*vec2(e.y-w.y,n.y-s.y);
    if (iFrame < 1)
   	Q = vec4(0,0,0,0);
    vec2 M = 0.5*R;
    if (iMouse.z>0.) M = iMouse.xy;
    Q.y += 7e-4*R.x*sin(float(iFrame)/70.)*10.*exp(-3.*dot(U-M,U-M)/R.x);
}

vec4 P (vec2 U, vec3 p) {
	return exp(-0.5*dot(U-p.xy,U-p.xy))*max(cos(p.z*6.2+vec4(1,2,3,4)),0.);
}
Main {

    if (iFrame < O) Q = vec4(0);
    else {
    Q = B(U);
    #define N 250
    for (int i = 0; i < N; i++) {
        int I = (iFrame-O)*N+i;
        vec4 h = hash44(vec4(U,i,iFrame));
    	vec3 p = 0.5*vec3(
            (I%int(2.*R.x)),
            (I/int(2.*R.x))%int(2.*R.y),
            2.*h.x
        );p.xy += h.xy*2.-1.;
        vec2 g = A(p.xy).zw;
        p.xy += 30.*g*(1.+p.z);
        Q += P(U,p.xyz);
    }
    }
}

