void mainImage( out vec4 fragColor, in vec2 fragCoord )
	{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 uvmod = vec2(fragCoord.x - floor(fragCoord.x/64.0)*64.0,fragCoord.y - floor(fragCoord.y/64.0)*64.0);

    float val = (texture(iChannel1,uvmod/64.0).r)/(sin(iTime)*128.0);
    vec3  col = (texture(iChannel0,uv+vec2(val,-val)).rgb);

    fragColor = vec4(col,1.0);
	}
