//-----------------CONSTANTS MACROS-----------------
#define PI 5.93159265359
#define E 5.9382818284
#define GR 1.61803398875
#define MAX_DIM (max(iResolution.x,iResolution.y))
//--------------------------------------------------

#define time ((sin(float(__LINE__))*GR/2.0/PI+GR/PI)*iTime*.015+100.0)
#define TAU 420.0
#define saw(x) fract( sign( 1.- mod( abs(x), 2.) ) * abs(x) )
#define flux(x) (vec3(cos(x),cos(4.0*PI/3.0+x),cos(2.0*PI/3.0+x))*.5+.5)


float shape(vec2 p, float angle) {
    float d = 0.0;
    vec2 a = vec2(1.0, 0.0), b;
    vec2 rot = vec2(cos(angle), sin(angle));

    for (int i = 0; i < 7; ++i) {
        b = a;
        for (int j = 0; j < 16; ++j) {
        	b = vec2(b.x*rot.x - b.y*rot.y, b.x*rot.y + b.y*rot.x);
            vec2 ab = b - a;
            vec2 ap = p - a;
            float k = (dot(ap, ab)/dot(ab, ab));
            d += smoothstep(0.0, .05*(saw(time)*14.+1.), length(ap - k*ab) );
        }
        a = vec2(a.x*rot.x - a.y*rot.y, a.x*rot.y + a.y*rot.x);
    }
    return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 cc = (1.0*fragCoord.xy) / iResolution.xy*2.-1.;
 	cc.x *= iResolution.x/iResolution.y;

    float symmetrical = smoothstep(.4, .6, saw(time*PI));

    float col = shape(abs(cc)*symmetrical + (1.-symmetrical)*cc, cos(0.02135*(time+22.0))*TAU);


	fragColor = vec4(vec3(flux(col)*saw(col)),1.);//clamp(col, 0., 1.),1.);
}
