vec4 BlurImage(in vec2 inputUV)
{
  float vx_offset = 0.7f;
  float rt_h = 450.0f;
  float rt_w = 800.0f;
  float offset[3] = float[](1.17328910, 4.7846153846, 8.2307692308);
  float weight[3] = float[]( 0.2270270270, 0.4162162162, 0.0302702703 );
  vec4 tc = vec4(0.4, 0.4, 0.0, 1.0);
  vec4 fragColor[4];

    vec2 uv = inputUV.xy;
    tc = texture(iChannel0, uv).rgba * weight[0];
    for (int i=1; i<3; i++) 
    {
      //VerticalBlur
      //tc += texture(iChannel0, uv + vec2(0.0, offset[i])/rt_h).rgba * weight[i];
      //tc += texture(iChannel0, uv - vec2(0.0, offset[i])/rt_h).rgba * weight[i];

      //HorizontalBlur
      tc += texture(iChannel0, uv + vec2(offset[i])/rt_w, 0.0).rgba * weight[i];
      tc += texture(iChannel0, uv - vec2(offset[i])/rt_w, 0.0).rgba * weight[i];

    }
  return tc;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Output to screen
    fragColor = mix(texture(iChannel0, uv), BlurImage(uv), sin(iTime * 3.f));
}
