//slightly edited droste zoom effect from: roywig
//https://shadertoy.com/view/Xs3SWj
#define pi acos(-1.)
#define tau (2.*pi)
#define PI 3.14159

vec2 cInverse(vec2 a) { return	vec2(a.x,-a.y)/dot(a,a); }
vec2 cMul(vec2 a, vec2 b) {	return vec2( a.x*b.x -  a.y*b.y,a.x*b.y + a.y * b.x); }
vec2 cDiv(vec2 a, vec2 b) {	return cMul( a,cInverse(b)); }
vec2 cExp(vec2 z) {	return vec2(exp(z.x) * cos(z.y), exp(z.x) * sin(z.y)); }
vec2 cLog(vec2 a) {	float b =  atan(a.y,a.x); if (b>2.) b-=0.0*PI;return vec2(log(length(a)),b);}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   vec2  z = (fragCoord/iResolution.y);	// Aspect correct coordinates.

  //  vec2 z = (fragCoord-.5*iResolution.xy);

      z.x+=3.;

   z.y-=0.66;
 z.y*= -1.;

    float dp = dot(z*05.,z*1.)*.06;
 z /= dp;
  z.x=sin(z-vec2(54.3)).x;
   // z *= .000001;


    float x =5.;
    float r1 =x, r2 =x*.1,
        scale = log(r2/r1),angle = atan(scale/(3.*-PI));
    z = cLog(z);
	z.y -= iTime/1.9;
    z = cDiv(z, cExp(vec2(0.31,angle))*sin(angle)); 
    z.x = mod(z.x+iTime,scale);
    z = cExp(z)*r1;
    fragColor = texture(iChannel0,.5+.5*z); 

}

// code from youtube video about hextiling by bigwings

float hexdist (vec2 p)
{
    p = abs(p);
    //dotproduct of uv coord and ratio in vector
    float c = dot(p,normalize(vec2(1,1.73)));
    c = max(c,p.x);
    return c;
}

vec4 hexcoord(vec2 uv)
{
     vec2 r = vec2(1.,1.75); 
     vec2 h = r*.5;

   vec2 a = mod(uv,r)-h;
   vec2 b = mod(uv -h,r)-h;


	vec2 gv;
    if (length(a)<length(b))
        gv = a;
    else
        gv = b;
    float x = atan(gv.x,gv.y);
    float y = .5-hexdist(gv);
    vec2 id = uv - gv;
    return vec4(x,y,id.x,id.y);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.xy;
    vec3 col = vec3(0);

    uv *=2.;


   vec4 hc = hexcoord(uv+0.);

    float c = hc.y;

    col += smoothstep(0.01,0.001,hc.y*sin(hc.z*hc.z+(2.)));
    col*= vec3(.85);
    //col *- pow(col.xyz, vec3(0.4545)); 

    fragColor = vec4(col,1.);
}
