void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // 0...1 depending on the current coordinate
    vec2 uv = fragCoord.xy / iResolution.xy;
    // -0.5...0.5, to get the middle of the screen
    uv -= 0.5;
    // * with the aspect ratio of the sreen
    uv.x *= iResolution.x/iResolution.y;

    // Get the distance so we know if we want to draw the circle
    float d = length(uv);
    // On default draw full black
    float c = 0.;

  	// Draw white
    if(d < 0.2) {
        c = 1.;
    }

	// Parameters for the Box
    float width = 250.0;
    float height = 250.0;
    float cX = fragCoord.x;
    float cY = fragCoord.y;

    // Draw grey
	if( cX < width && cY < height && cX > 150. && cY > 150.) {
        c = 0.3;
    }

	fragColor = vec4(vec3(c), 1.0);
}
