void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 center = vec2(iMouse.x, iMouse.y);
    float dist = distance(center, fragCoord);


    vec3 col = vec3(0.0, dist / iResolution.y, 0.0);

    float swidth = 50.0;

    float circ = mod(dist - iTime*10., swidth) / swidth/2.0;

    //float wv = pow(sin(dist * 0.2 - (iTime * 10.)), 0.5);
    float wv = sin(dist  / 10. - iTime) + sin(iTime/3.) + 0.5;
    float r = wv;
    float g = 1. - wv * dist / iResolution.y;
    float b = dist / iResolution.x * (cos(iTime/1.) + 0.3);
    col = vec3(r, g, b);

	fragColor = vec4(col,1.0);
}
