const float z = 15.;
const float complexity = 8.;
const float speed = 1./3.;
const float density = .4; // 0-1

const float PI = atan(1.)*4.;

vec4 hash42(vec2 p)
{
	vec4 p4 = fract(vec4(p.xyxy) * vec4(.1031, .1030, .0973, .1099));
    p4 += dot(p4, p4.wzxy+33.33);
    return fract((p4.xxyz+p4.yzzw)*p4.zywx);
}

mat2 rot2D(float r){
    return mat2(cos(r), sin(r), -sin(r), cos(r));
}
#define q(x,p) (floor((x)/(p))*(p))

void mainImage( out vec4 o, vec2 C)
{
    vec2 R = iResolution.xy;
    vec2 uv = C/R.xy;
    vec2 N = uv;
    uv.x *= R.x/R.y;
    uv.x += floor(uv.y/.2)*.5;
    uv *= z;
    float t = iTime;
    uv += floor(t*speed)*z;
    o = vec4(1);

    for (float i = 1.;i <= complexity; ++ i) {
        vec2 c = floor(uv+i);
        vec4 h = hash42(floor(uv+i));
        vec2 p = fract(uv+i)-.5;
        uv += p * h.z;
        h = hash42(floor(uv));
        h = fract(h+.1*length(p+q(iTime,.1)*.5)); // animate
        if (i < 2. || h.w > density) {
            o *= h+.0;
        }
    }
    o=step(.5,o) * mod(C.y,3.)/2.;
}
