void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //Centered uv position
    vec2 uv = vec2(0.5,0.5) + 0.5 * (2.0 * fragCoord.xy/iResolution.xy - vec2(1,1));

    float invertAspectRatio = iResolution.x/iResolution.y;
    uv.x = uv.x * invertAspectRatio;
    vec2 dist = uv - vec2(0.5 * invertAspectRatio,0.5);

    float sphereRadius = 0.25;

    vec4 outpo = vec4(0,0,0,1);
    if(sqrt(dist.x*dist.x + dist.y*dist.y) < sphereRadius)
        outpo = vec4(1,1,1,1);

    // Output to screen
    fragColor = outpo;
}
