void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //vec2 uv = fragCoord/300.0;
    vec2 uv = fragCoord/iResolution.xy;
    vec4 a;
    float pi=3.14159;
    float r=sqrt(pow(uv.x-0.5,2.0)+pow(uv.y-0.5,2.0));
    float o=acos((uv.x-0.5)/r);
    o=o/(2.0*pi);
    if (uv.y<0.5)
        o=(1.0-o);
    float o1=o-iTime*0.5;
    float o2=o+iTime*0.5;

    o1=mod(o1,1.0);
    o2=mod(o2,1.0);

    a.r=r*2.0/sqrt(2.0);
    a.b=1.0-a.r;
    //

    a.b=mod(a.b-o1,0.1)*10.0;
    a.r=mod(a.r-o2,0.1)*10.0;
    //
    fragColor = vec4(a.b,a.r,a.b,1.0);
}
