const float GAMMA   = 2.2;
const float ZOOM    = 0.35;
const float SAMPLES = 12.0;


vec3 rotateY(vec3 p, float angle)
{
    float cRot = cos(angle);
    float sRot = sin(angle);
    return vec3(p.x * cRot - p.z * sRot, 
                p.y, 
                p.x * sRot + p.z * cRot);
}

vec3 sampleMovie(vec2 p)
{
    p.x =  mod(p.x - 2.0, 4.0) - 2.0;
    p.y = -mod(p.y - 1.1, 2.2) + 1.1;
    vec2 sampleP = (p - vec2(-1.5, -1.0)) * vec2(0.33, 0.5);
    return texture(iChannel0, sampleP).rgb;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 cs = (fragCoord.xy - iResolution.xy * 0.5) / max(iResolution.x, iResolution.y);

    vec3 color = vec3(0.0);
    float t = 10.0;

    //Camera
    vec3 camOri = vec3(0.0);
    vec3 camDir = vec3(1.0);

    camDir = rotateY(normalize(vec3(cs.x  * ZOOM, cs.y * ZOOM, -1.0)), 0.0);

    vec3 albedo = vec3(1.0, 0.5, 0.5);
    vec3 emissive = vec3(0.0);

    vec3 p = camOri + camDir * t;
    emissive = sampleMovie(p.xy);

    float sampleScale = 1.0 / SAMPLES;
    color = emissive + albedo * sampleScale;

    fragColor = vec4(color, 1.0);
}
