void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 R = iResolution.xy,
      // U = fragCoord / R.y;                     // [0,1] vertically
      // U = ( 2.*fragCoord - R ) / R.y;          // [-1,1] vertically
      // U = ( fragCoord - .5*R ) / R.y;          // [-1/2,1/2] vertically
      U = ( 2.*fragCoord - R ) / min(R.x,R.y); // [-1,1] along the shortest side    

   	vec2 st = U/R;
    float w = 10000000.;

    st*= w;

    vec2 ipos=floor(st)/w;

    int x = int(ipos.x*w);
    int y = int(ipos.y*w);

    float t = float((x+y)^(x-y));

    float z = 1995.;
    float j = mod(pow(t, 6.), z);

    float k = j/w;

    float r=k;
    float g=k;
    float b=k;

    fragColor=vec4(r,g,b,1.);

}
