void mainImage(out vec4 O, vec2 C) {
	float i=1.,h=0.,e,s,k;
    for(;++i<99.;){
        vec3 R=iResolution,
        p=h*normalize(vec3((C-.5*R.xy)/R.y,1))+vec3(0,1,iTime);
		s=2.;
        p=abs(mod(p-1.,2.)-1.)-1.;
        for(int j=0;++j<9;)
            p=1.-abs(p-1.),
            p=p*(k=-1./dot(p,p))-vec3(.1,.3,.1),
            s*=abs(k);
        h+=e=length(p.xz)/s;
        if(e<.003)break;
	}
    O+=12./i-O;
}
