float sphereSdf(vec2 center, float radius, vec2 pos){
	return length(center - pos) - radius;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float radius = iResolution.y * .4 * (sin(iTime) * .5 + 1.);
    float sphere = sphereSdf(vec2(iResolution / 2.), radius, fragCoord);

    vec3 col = vec3(sin(sphere * sin(iTime)));

    col += vec3(abs(sphere) * .3, 0, 0);

    fragColor = vec4(col,1.0);
}
