//A float version of random used in value noise
float random (in vec2 st) {
    return fract(sin(dot(st.xy,
                         vec2(12.9898,78.233)))
                * 43758.5453123);
}

/*
What this does is giving a fixed random number to each vertice in
a cell and the interpolates each number in the cell according to
the 4 vertice numbers given by the rand function.

To then get the distorted effect, we rotate the uv coordinate
with this interpolated value and therefore getting a smooth
curve because while uv-coordinates are distorted, they are so
according to a smooth interpolation which makes their distortion
look smooth.

The uv distortion happens on row
'rotate2D(noise(pos+iTime*0.4))*pos';
We multiply the uv coord (pos) with the rotation matrix.
*/
float noise(vec2 uv) {
    vec2 id = floor(uv);
    vec2 f = fract(uv);

    vec2 u = f*f*(3.-2.*f);

    //Bi-linear interpolation
    return mix(mix(random(id), random(id+vec2(1., 0.)), u.x),
        	mix(random(id+vec2(0., 1.)), random(id+vec2(1., 1.)), u.x)
        , u.y);
}

mat2 rotate2D(float angle) {
    //angle += iTime*0.1;
	return mat2(cos(angle), -sin(angle), sin(angle), cos(angle));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
	vec2 pos = uv*10.-vec2(0.5)*9.; //Vi vill ha unika rutor s√• skippa <fract>(uv*10.)

    vec2 col = rotate2D(noise(pos+iTime*0.4))*pos;


    // Output to screen
    fragColor = vec4(vec3(col*.6, 0.6),1.0);
}
