void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = trunc(fragCoord.xy);
    vec2 limit = trunc(iResolution.xy - vec2(1.0));    

    if (uv.x >= limit.x ||
        uv.x <= 0.0 ||
		uv.y >= limit.y ||
        uv.y <= 0.0) {
    	fragColor = vec4(0.0, 1.0, 0.0, 1.0);   
    } else {    
    	fragColor = vec4(0.0, 0.0, 0.0, 1.0);
    }
}
