float random (in vec2 st, vec2 coeff, float scaler) {
    return fract(sin(dot(st.xy, coeff) * scaler));
    }

float noise (in vec2 st, vec2 coeff, float scaler) {
    vec2 i = floor(st);
    vec2 f = fract(st);

    // Four corners in 2D of a tile
    float a = random(i, coeff, scaler);
    float b = random(i + vec2(1.0, 0.0), coeff, scaler);
    float c = random(i + vec2(0.0, 1.0), coeff, scaler);
    float d = random(i + vec2(1.0, 1.0), coeff, scaler);

    // Smooth Interpolation

    // Cubic Hermine Curve.  Same as SmoothStep()
    vec2 u = f*f*(3.0-2.0*f);
    // u = smoothstep(0.,1.,f);

    // Mix 4 coorners percentages
    return mix(a, b, u.x) +
            (c - a)* u.y * (1.0 - u.x) +
            (d - b) * u.x * u.y;
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec2 uvhalf = uv.xy / 2. * 0.5;

    float centerDist = distance(uv.xy, uvhalf);

    float pos = noise(uv.xy * 0.4 + 1.3 * sin(dot(1.-uv.yx, vec2(centerDist)) * centerDist + iTime * 0.75161), 
                      vec2(431.51,3330.311),
                      23.21 + centerDist);

    // Time varying pixel color
    vec3 col = iTime * pos / centerDist * vec3(uv.xy, sin(centerDist * 4. * radians(360.) + 3.))+vec3(0.10,0.3,0);

    // Output to screen
    fragColor = vec4(1. / col * vec3(0.33 * sin(iTime * 0.0014 * centerDist) + 0.251,
                                0.144 + centerDist,
                                centerDist),
                     1.0);
}
