void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    int blur = 5;

    vec2 uv = fragCoord/iResolution.xy;

    int x = (blur * 2) + 1;
    int y = x;

    vec4 total = vec4(0.0, 0.0, 0.0, 0.0);
    while(x > 0){
        while(y > 0){
            total += texture(iChannel0, vec2(fragCoord.x + float(x) - float(blur * 2),
                                             fragCoord.y + float(y) - float(blur * 2))/iResolution.xy);
            y = y - 1;
        }
        y = (blur * 2) + 1;
        x = x - 1;
    }

    fragColor = total / float(((blur * 2) + 1) * ((blur * 2) + 1));
}
