vec2 dist(vec2 uv)
{
    float a = (texture(iChannel1, uv/3.+iTime*.05).r)-.5;
    return vec2(uv+a*.1);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.y;
    uv.x += iTime*.3;
    uv.y += sin(iTime*.5)*.2;

    float a = clamp(0., 1., texture(iChannel1, uv/10.).r/8.);

    float d = pow(uv.y, 3.);
    vec2 nuv = mix(uv*2., dist(uv*2.), d);

    vec3 c = texture(iChannel0, nuv).rgb;
    vec3 col = mix(vec3(1), vec3(0.1, 0.7, 1.), pow(d, 3.));

    vec3 final = c*col*(1.-d*.2);

    fragColor = vec4(pow(final, vec3(1.5)),1.0);
}
