// normal calculation
// vec3(SDF(p-epsilon.xyy),(SDF(p_epsilon.yxy), SDF(p-epsilon.yyx))



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 centered_uv = (2.*fragCoord-iResolution.xy)/iResolution.y;
    centered_uv = abs(centered_uv*sin(iTime)*cos(iTime));

    vec4 fft = texture(iChannel0, vec2(centered_uv.x*sin(iTime)*0.2+centered_uv.y*cos(iTime)*0.2,0.25 ) );

    fft = vec4( smoothstep(0.5,0.8,fft) );

    vec3 color = fft.x * vec3(abs(sin(iTime/2.)),abs(cos(iTime)),abs(tan(iTime)));

    fragColor = vec4(color,1.);
}
