vec2 mul(vec2 a, vec2 b)
{
    return vec2(a.x * b.x - a.y * b.y, a.x * b.y + a.y * b.x);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - .5 * iResolution.xy) / iResolution.y;
    uv *= 1.2;

    uv = mul(uv, uv) + vec2(.5, .9);
    uv = mul(uv, uv) + vec2(cos(radians(30.) * iTime), sin(radians(35.) * iTime));
    uv = mul(uv, uv) + vec2(10., -2.);
    uv = mix(uv, normalize(uv), 1.5);
    uv.x = abs(uv.x) - 5.;
    uv.y = abs(uv.y) - 3.;
    uv = mul(uv, uv) + 0. * iTime;

    uv = mix(uv, 3. * uv / normalize(uv), sin(radians(15.) * iTime));

    vec3 col = 0.5 + 0.5*cos(radians(360.) * iTime+uv.xyx+vec3(0,2,4));
    fragColor = vec4(col,1.0);
}
