// regolfing 2017' shader https://shadertoy.com/view/XlVXRW ( 305 chars )

#define A atan(U.y,U.x)                   //
void mainImage(out vec4 O, vec2 u) {
    vec2  R = iResolution.xy,
          U = ( u+u - R ) / R.y;
    U.y += .1;
    float p = .1592,
          c = length( U = 3.* U* mat2(cos( floor(3.*A*p-p)/3./p +.05+ vec4(0,11,33,0)))
                         - vec2(0,1.73) );
    O += c + fract( A*p + .4 ) > 2.
           ? c
           :  min( 1., abs( c = 5.* sin( A + c/p ) ) )
            - max( 0.,      c - 3. );
}
