// The step function: zero in the right half of the screen, 1.0 above 
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Step return 0 if fragCoord.x is less then the half 
    //  screen resolution 1.0 otherwise
    float c = step(iResolution.x/2.0, fragCoord.x);
    fragColor = vec4(c, c, c, 0.0);
}
