const float h=1.;	//advection step size
const float dt=.25;
const float k=5e-3;	//mouse fluid generation radius
					//(not actual radius)

#define R iResolution.xy
#define M iMouse

#define A(u) texture(iChannel0,(u)/R)
#define B(u) texture(iChannel1,(u)/R)

#define colors vec3(sin(iTime),sin(iTime*1.6180),sin(iTime*2.7182))*0.5+0.5

float dot2(vec2 k){return dot(k,k);}

#define RK4 vec2 k1=B(U).xy,k2=B(U-0.5*h*k1).xy,k3=B(U-0.5*h*k2).xy,k4=B(U-h*k3).xy;
#define V h*(k1+2.*(k2+k3)+k4)/6.;

void mainImage(out vec4 Q,in vec2 U){
    RK4
	U-=V;
    Q=A(U);

    Q.w-=dt*(B(U+vec2(1,0)).x-B(U-vec2(1,0)).x+B(U+vec2(0,1)).y-B(U-vec2(0,1)).y);

    if(M.z>0.)
        Q+=dt*vec4(colors,.2)*exp(-k*dot2(M.xy-U));

	if(iFrame==0)
        Q=vec4(3,0,0,.1)*exp(-k*dot2(U-.5*R.xy));
}

void mainImage(out vec4 Q,in vec2 U){
    Q.zw=B(U).zw;
    RK4
    U-=V;
    Q.xy=B(U).xy;

    Q.xy-=dt*vec2(A(U+vec2(1,0)).w-A(U-vec2(1,0)).w,A(U+vec2(0,1)).w-A(U-vec2(0,1)).w);


    if(M.z>0.){
        if(all(equal(Q.zw,vec2(0)))||all(equal(Q.zw,M.xy)))
        	Q.xy+=.5*dt*vec2(
        	    sin(iTime*2.),
        	    cos(iTime*2.)
        	)*exp(-k*dot2(M.xy-U));
        else
            Q.xy+=.15*dt*(M.xy-Q.zw)*exp(-k*dot2(M.xy-U));
    	Q.zw=M.xy;
    }else Q.zw=vec2(0);

	if(iFrame==0)
        Q.xy=vec2(0,1.)*exp(-k*dot2(U-R.xy*.5));
}
