float EPS;
const vec3 CB=vec3(0);
const vec3 CO=vec3(1.,.55,.06);
const vec3 CR=vec3(.44,.06,0.);
const vec3 CC=vec3(.34,.82,1.);
const float ROR=.39682539682;
const float RIR=.29761904761;
const float CRW=.0238095238;
const vec3 M1=vec3(-.375,0.37301587301,.08333333333);
const vec3 M2=vec3(.40277777777,-.34126984127,.05555555555);

float sstep(float x){return smoothstep(-EPS,EPS,x);}
vec3 csstep(float x,vec3 a,vec3 b){return mix(b,a,sstep(x));}
vec3 cstep(float x,vec3 a,vec3 b){return mix(b,a,step(0.,x));}

float circ(vec2 p,float r){return length(p)-r;}
float ring(vec2 p,float r0,float r1){return max(-circ(p,r0),circ(p,r1));}
float cres(vec2 p,float r,vec2 o){return max(circ(p,r),-circ(p-o,r+EPS));}

vec3 flag(vec2 p){
    vec3 y0=csstep(circ(p-M1.xy,M1.z),CO,CB);
    vec3 y1=csstep(circ(p-M2.xy,M2.z),CB,CO);
    vec3 c=cstep(p.y,y0,y1);
    c=csstep(ring(p,RIR,ROR),c,CR);
    c=csstep(cres(p,RIR,vec2(CRW,0.)),c,CC);
    return c;
}

void mainImage(out vec4 fragColor,in vec2 fragCoord)
{
    EPS=2./length(iResolution);
    fragColor=vec4(flag((fragCoord-.5*iResolution.xy)/iResolution.y),1.);
}
