// Fork of "Refraction-Like Effect" by oneshade. https://shadertoy.com/view/WstcRH

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 displacementSampleUV = vec2(fragCoord.x + (iTime+20.) * 75.0, fragCoord.y) / iChannelResolution[0].xy;

    float da = iMouse.x/iResolution.x;
    float dd = iMouse.y/iResolution.y * 8.;

    float displacement = texture(iChannel0, displacementSampleUV).x * da * dd;

    vec2 displacementDirection = vec2(cos(displacement * 6.28318530718), sin(displacement * 6.28318530718));
    vec2 displacedUV = (fragCoord + displacementDirection * displacement * 20.0) / iResolution.xy;
    float shade = texture(iChannel1, displacedUV).x;
    fragColor = vec4(shade, shade, shade, 1.0);
}
