//https://www.youtube.com/watch?v=dKA5ZVALOhs&ab_channel=TheArtofCode

float DistLine(vec3 ro, vec3 rd, vec3 p)
    {
        return length(cross(p-ro, rd))/length(rd);
    }

//positions and colours
const vec2 points[8] = vec2[8](
    vec2(0.1, 0.2),
    vec2(0.9, 0.8),
    vec2(0.3, 0.3),
    vec2(0.7, 0.5),
    vec2(0.8, 0.3),
    vec2(0.6, 0.6),
    vec2(0.3, 0.1),
    vec2(0.4, 0.5)
);
const vec4 colors[8] = vec4[8](
    vec4(0.5, 0.3, 0.9, 1.0),
    vec4(0.2, 0.7, 0.2, 1.0),
    vec4(0.7, 0.5, 0.1, 1.0),
    vec4(0.8, 0.9, 0.3, 1.0),
    vec4(0.3, 0.8, 0.7, 1.0),
    vec4(0.4, 0.1, 0.8, 1.0),
    vec4(0.6, 0.4, 0.4, 1.0),
    vec4(0.9, 0.2, 0.7, 1.0)
);

const float a[8] = float[8](0.3, 0.2, 0.1, -0.4, -0.3, 0.1, 0.05, 0.4);
const float b[8] = float[8](0.2, 0.8, 0.1, 0.7, -0.2, -0.5, 0.9, -0.6);
const float c[8] = float[8](2.0, 3.0, 6.0, 3.0, 1.0, 3.0, 1.0, 1.0 );

vec2 GetPoint(in int i )
{
    // move the points on orbits
    float x = points[i].x + a[i]*sin(c[i]*iTime*0.1);    
    float y = points[i].y + b[i]*cos(c[i]*iTime*0.1); 
    return vec2(x,y);
}

//adds the bacground colours
vec4 GetVoronoiColor( in vec2 point )
{
    float nearDistSq = 1e20;
    vec4 color;
    for ( int i = 0; i < 8; i++ )
    {
        vec2 diff = point-GetPoint(i);
        float d2 = dot(diff,diff);
        if ( d2 < nearDistSq )
        {
            nearDistSq = d2;
            color = colors[i];
        }
    }
    return color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float blur = 0.7f;

    // Output to screen
    fragColor = GetVoronoiColor(uv);

    //workds out the shape of the circe
    uv -= 0.5;
    uv.x *= iResolution.x/iResolution.y;
    //uv.y -= abs(uv.x)*0.5;
    uv.x -= sqrt(abs(uv.y))*0.5;
    //uv.x -= sqrt(abs(uv.y));

    vec3 ro = vec3(0, 0, -2);
    vec3 rd = vec3(uv.x, uv.y, 0)-ro;

    float t = iTime;
    vec3 p = vec3(sin(t), 0, 1.0+cos(t));
    float d = DistLine(ro, rd, p);

    //adds the circle on top of the changing background
    d = smoothstep(0.1, 0.09, d);
    fragColor += vec4(d);

}
